@charset "UTF-8";
/* Enhanced Job Application Form Styles */
* {
  font-family: sans-serif !important;
  box-sizing: border-box;
}

body {
  background: #F8FAFC !important;
  padding: 20px;
  margin: 0;
  min-height: 100vh;
  position: relative;
}

.container {
  width: 100%;
  margin: 0;
  position: relative;
  z-index: 1;
}

.header {
  text-align: left;
  margin-bottom: 2rem;
  animation: fadeInDown 0.8s ease-out;
}

h1 {
  font-size: 24px; /* Changed to 24px */
  font-weight: 700;
  color: #000000;
  margin-bottom: 0.5rem;
  text-shadow: none;
}

.header p {
  color: #64748b;
  font-size: 16px; /* Changed to 16px */
  margin-bottom: 0;
  text-shadow: none;
  text-align: left;
}

.cards-container {
  display: flex;
  gap: 2rem;
  align-items: flex-start;
}

.card {
  background: rgba(255, 255, 255, 0.95);
  backdrop-filter: blur(20px);
  border: 1px solid rgba(255, 255, 255, 0.2);
  border-radius: 20px;
  box-shadow: 0 20px 40px rgba(0, 0, 0, 0.15);
  margin-bottom: 2rem;
  overflow: hidden;
  transition: all 0.3s ease;
  animation: fadeInUp 0.8s ease-out;
}

.job-selection-card {
  flex: 0 0 35%;
}

.application-info-card {
  flex: 1;
}

.card:hover {
  transform: translateY(-2px);
  box-shadow: 0 25px 50px rgba(0, 0, 0, 0.2);
}

.card-header {
  background: linear-gradient(135deg, #f8fafc 0%, #e2e8f0 100%);
  padding: 1.5rem;
  border-bottom: 1px solid rgba(0, 0, 0, 0.05);
  display: flex;
  justify-content: space-between;
  align-items: flex-start;
  position: relative;
}

.card-header::before {
  content: "";
  position: absolute;
  top: 0;
  left: 0;
  right: 0;
  height: 3px;
  background: linear-gradient(90deg, #10b981, #34d399, #10b981);
  background-size: 200% 100%;
  animation: shimmer 3s infinite;
}

.card-header-content h2 {
  font-size: 1.25rem;
  font-weight: 600;
  margin: 0 0 0.5rem 0;
  display: flex;
  align-items: center;
  gap: 0.75rem;
  color: #1e293b;
}

.card-header-content p {
  font-size: 0.875rem;
  color: #64748b;
  margin: 0;
  line-height: 1.5;
}

.icon-wrapper {
  width: 40px;
  height: 40px;
  border-radius: 12px;
  background: linear-gradient(135deg, #10b981, #34d399);
  display: flex;
  align-items: center;
  justify-content: center;
  color: white;
  box-shadow: 0 4px 12px rgba(16, 185, 129, 0.3);
}

.card-content {
  padding: 2rem;
}

label {
  font-size: 0.875rem;
  font-weight: 600;
  display: block;
  margin-bottom: 0.5rem;
  color: #374151;
  position: relative;
}

.required-field::after {
  content: "*";
  color: #ef4444;
  margin-left: 4px;
}

input, select, textarea {
  width: 100%;
  border: 2px solid #e5e7eb;
  border-radius: 12px;
  padding: 0.875rem 1rem;
  font-size: 0.875rem;
  margin-bottom: 1.5rem;
  background: white;
  transition: all 0.3s ease;
  font-family: inherit;
}

input:focus, select:focus, textarea:focus {
  outline: none;
  border-color: #3b82f6;
  box-shadow: 0 0 0 3px rgba(59, 130, 246, 0.1);
  background: #fafbff;
}

select {
  cursor: pointer;
  background-image: url('data:image/svg+xml,<svg xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24" stroke="%233b82f6"><path stroke-linecap="round" stroke-linejoin="round" stroke-width="2" d="M19 9l-7 7-7-7"/></svg>');
  background-repeat: no-repeat;
  background-position: right 1rem center;
  background-size: 1.25rem;
  appearance: none;
}

.btn {
  padding: 0.875rem 1.5rem;
  border-radius: 12px;
  cursor: pointer;
  font-size: 0.875rem;
  font-weight: 600;
  border: none;
  transition: all 0.3s ease;
  display: inline-flex;
  align-items: center;
  gap: 0.5rem;
  text-decoration: none;
  font-family: inherit;
  position: relative;
  overflow: hidden;
}

.btn::before {
  content: "";
  position: absolute;
  top: 0;
  left: -100%;
  width: 100%;
  height: 100%;
  background: linear-gradient(90deg, transparent, rgba(255, 255, 255, 0.3), transparent);
  transition: left 0.5s;
}

.btn:hover::before {
  left: 100%;
}

.btn-outline {
  border: 2px solid #e5e7eb;
  background: white;
  color: #374151;
}

.btn-outline:hover {
  border-color: #10b981;
  background: #f0fdf4;
  color: #10b981;
  transform: translateY(-1px);
  box-shadow: 0 4px 12px rgba(16, 185, 129, 0.15);
}

.btn-primary {
  background: linear-gradient(135deg, #3b82f6 0%, #1d4ed8 100%);
  color: white;
  box-shadow: 0 4px 12px rgba(59, 130, 246, 0.3);
}

.btn-primary:hover {
  transform: translateY(-2px);
  box-shadow: 0 8px 24px rgba(59, 130, 246, 0.4);
}

.btn:disabled {
  opacity: 0.6;
  cursor: not-allowed;
  transform: none !important;
  box-shadow: none !important;
}

.btn:disabled::before {
  display: none;
}

.profile-field {
  padding: 1rem;
  background: linear-gradient(135deg, #f8fafc 0%, #f1f5f9 100%);
  border: 1px solid #e2e8f0;
  border-radius: 12px;
  margin-bottom: 1rem;
  font-size: 0.875rem;
  line-height: 1.6;
  transition: all 0.3s ease;
}

.profile-field:hover {
  background: linear-gradient(135deg, #f1f5f9 0%, #e2e8f0 100%);
  transform: translateX(4px);
}

.profile-field b {
  color: #475569;
  display: inline-block;
  min-width: 140px;
}

.profile-field-row {
  display: flex;
  gap: 1.5rem;
  margin-bottom: 1.5rem;
}

.profile-field-item {
  flex: 1;
}

.profile-field-item > input,
.profile-field-item.profile-field {
  margin-bottom: 0;
}

.card-content .profile-field:last-child {
  margin-bottom: 0;
}

.selected-box {
  padding: 1.25rem;
  background: linear-gradient(135deg, #ecfdf5 0%, #d1fae5 100%);
  border: 2px solid #10b981;
  border-radius: 12px;
  margin-top: 1rem;
  font-size: 0.875rem;
  position: relative;
  animation: slideIn 0.5s ease-out;
}

.selected-box::before {
  content: "✓";
  position: absolute;
  top: 0.75rem;
  right: 1rem;
  width: 24px;
  height: 24px;
  background: #10b981;
  color: white;
  border-radius: 50%;
  display: flex;
  align-items: center;
  justify-content: center;
  font-weight: bold;
  font-size: 12px;
}

.selected-box b {
  color: #065f46;
  display: block;
  font-size: 1rem;
  margin-bottom: 0.5rem;
}

.submit-section {
  position: absolute;
  top: 28px;
  right: 20px;
  background: none;
  padding: 0;
  margin: 0;
  backdrop-filter: none;
  box-shadow: none;
  border-radius: 0;
  z-index: 10;
}

.edit-buttons {
  display: flex;
  gap: 0.75rem;
}

/* Animations */
@keyframes fadeInDown {
  from {
    opacity: 0;
    transform: translateY(-30px);
  }
  to {
    opacity: 1;
    transform: translateY(0);
  }
}
@keyframes fadeInUp {
  from {
    opacity: 0;
    transform: translateY(30px);
  }
  to {
    opacity: 1;
    transform: translateY(0);
  }
}
@keyframes slideIn {
  from {
    opacity: 0;
    transform: translateX(-20px);
  }
  to {
    opacity: 1;
    transform: translateX(0);
  }
}
@keyframes shimmer {
  0% {
    background-position: -200% 0;
  }
  100% {
    background-position: 200% 0;
  }
}
/* Responsive Design */
@media (max-width: 1024px) {
  .cards-container {
    flex-direction: column;
  }
  .job-selection-card, .application-info-card {
    width: 100%;
    flex-basis: auto;
  }
}
@media (max-width: 768px) {
  .container {
    padding: 0 1rem;
  }
  h1 {
    font-size: 1.5rem; /* Adjusted for mobile */
  }
  .card-content {
    padding: 1.5rem;
  }
  .edit-buttons {
    flex-direction: column;
  }
  .btn {
    justify-content: center;
  }
  .submit-section {
    position: static;
    text-align: center;
    margin-top: 2rem;
    padding: 1rem;
    background: rgba(255, 255, 255, 0.5);
    border-radius: 20px;
    backdrop-filter: blur(10px);
  }
  .profile-field-row {
    flex-direction: column;
    gap: 0;
    margin-bottom: 0;
  }
  .profile-field-item > input {
    margin-bottom: 1.5rem;
  }
  .profile-field-item.profile-field {
    margin-bottom: 1rem;
  }
}
/* Focus states for accessibility */
.btn:focus-visible {
  outline: 2px solid #3b82f6;
  outline-offset: 2px;
}

input:focus-visible, select:focus-visible, textarea:focus-visible {
  outline: 2px solid #3b82f6;
  outline-offset: 2px;
}

/* Additional utility classes */
.fade-in {
  animation: fadeInUp 0.6s ease-out;
}

.slide-up {
  animation: slideUp 0.5s ease-out;
}

@keyframes slideUp {
  from {
    opacity: 0;
    transform: translateY(20px);
  }
  to {
    opacity: 1;
    transform: translateY(0);
  }
}

