   ============================ */
/* Target Font Awesome elements and restore the icon font */
i[class*=fa-],
span[class*=fa-],
[class^=fa-],
.fa, .fas, .far, .fab, .fa-solid, .fa-regular, .fa-brands {
  font-family: "Font Awesome 6 Free" !important;
  font-style: normal !important;
  font-variant: normal !important;
  text-transform: none !important;
  line-height: 1 !important;
  speak: none !important;
  -webkit-font-smoothing: antialiased !important;
  -moz-osx-font-smoothing: grayscale !important;
  display: inline-block !important;
}

/* Ensure correct weights for FA styles */
.fa-solid, .fas {
  font-weight: 900 !important; /* solid icons */
}

.fa-regular, .far {
  font-weight: 400 !important; /* regular icons */
}

.fa-brands, .fab {
  font-weight: 400 !important; /* brands use their own font-face */
}

/* Make sure pseudo-elements used by FA also get the font */
i[class*=fa-]::before,
span[class*=fa-]::before,
[class*=fa-]::before {
  font-family: "Font Awesome 6 Free" !important;
  -webkit-font-smoothing: antialiased !important;
  -moz-osx-font-smoothing: grayscale !important;
}

