// ===================================
// 1. HELPER FUNCTIONS
// (From Custom5)
// ===================================
function getUSDate(date = new Date()) {
  const month = String(date.getMonth() + 1).padStart(2, "0");
  const day = String(date.getDate()).padStart(2, "0");
  const year = date.getFullYear();
  return `${month}-${day}-${year}`;
}

// ===================================
// 2. WIDGET 1 (Custom3) LOGIC
// ===================================
class Sidebar {
  constructor() {
    this.sidebar = document.getElementById('sidebar');
    if (!this.sidebar) {
      console.error("Sidebar HTML element not found.");
      return;
    }
    this.navLinks = this.sidebar.querySelectorAll('.nav-link');
    // START: Additions for toggle
    this.toggleButton = document.getElementById('sidebar-toggle');
    this.mainLayout = document.getElementById('main-layout');
    // END: Additions for toggle
    this.init();
  }
  init() {
    this.setupEventListeners();
    // START: Addition for toggle
    this.setupToggle();
    // END: Addition for toggle
  }

  // START: New toggle function
  setupToggle() {
    if (this.toggleButton && this.mainLayout) {
      this.toggleButton.addEventListener('click', () => {
        this.mainLayout.classList.toggle('layout-collapsed');
      });
    }
  }
  // END: New toggle function

  setupEventListeners() {
    this.navLinks.forEach(link => {
      link.addEventListener('click', e => {
        e.preventDefault();
        const page = link.getAttribute('data-page');
        this.setActiveLink(link);

        // Fire a unique Appsmith event for each menu item
        if (typeof appsmith !== 'undefined' && appsmith.triggerEvent) {
          switch (page) {
            case 'jobs':
              appsmith.triggerEvent('onJobs', {
                page: 'jobs'
              });
              break;
            case 'messages':
              appsmith.triggerEvent('onMessages', {
                page: 'messages'
              });
              break;
            case 'profile':
              appsmith.triggerEvent('onProfile', {
                page: 'profile'
              });
              break;
            case 'help':
              appsmith.triggerEvent('onHelp', {
                page: 'help'
              });
              break;
            default:
              appsmith.triggerEvent('onSidebarNav', {
                page
              });
          }
        }
      });
    });
  }
  setActiveLink(activeLink) {
    this.navLinks.forEach(link => {
      link.classList.remove('active');
    });
    activeLink.classList.add('active');
  }
}

