class Sidebar {
	constructor() {
		this.sidebar = document.getElementById('sidebar');
		if (!this.sidebar) {
			console.error("Sidebar HTML element not found.");
			return;
		}
		this.navLinks = this.sidebar.querySelectorAll('.nav-link');
		// START: Additions for toggle
		this.toggleButton = document.getElementById('sidebar-toggle');
		this.mainLayout = document.getElementById('main-layout');
		// END: Additions for toggle
		this.init();
	}

	init() {
		this.setupEventListeners();
		// START: Addition for toggle
		this.setupToggle();
		// END: Addition for toggle
	}

	// START: New toggle function
	setupToggle() {
		if (this.toggleButton && this.mainLayout) {
			this.toggleButton.addEventListener('click', () => {
				this.mainLayout.classList.toggle('layout-collapsed');
			});
		}
	}
	// END: New toggle function

	setupEventListeners() {
		this.navLinks.forEach(link => {
			link.addEventListener('click', (e) => {
