						case 'jobs':
							appsmith.triggerEvent('onJobs', {
								page: 'jobs'
							});
							break;
						case 'messages':
							appsmith.triggerEvent('onMessages', {
								page: 'messages'
							});
							break;
						case 'profile':
							appsmith.triggerEvent('onProfile', {
								page: 'profile'
							});
							break;
						case 'help':
							appsmith.triggerEvent('onHelp', {
								page: 'help'
							});
							break;
						default:
							appsmith.triggerEvent('onSidebarNav', {
								page
							});
					}
				}
			});
		});
	}

	setActiveLink(activeLink) {
		this.navLinks.forEach(link => {
			link.classList.remove('active');
		});
		activeLink.classList.add('active');
	}
}

