// 3. WIDGET 2 (Custom1) LOGIC
// Renamed initApp to initProfileApp
// ===================================
function initProfileApp() {
	const appsmithEmail = typeof appsmith !== 'undefined' && appsmith.model && appsmith.model.email ?
				appsmith.model.email :
	"";
	const mockProfile = appsmith.model?.info?.[0] || {};
	let formData = {
		candidateName: mockProfile.candidateName || mockProfile.name || "",
		email: appsmithEmail,
		phone: mockProfile.phone || "",
		linkedin: mockProfile.linkedin || "",
		consent: mockProfile.consent || false,
		interviewSlot: Array.isArray(mockProfile.interviewSlot) ?
		mockProfile.interviewSlot :
		(mockProfile.interviewSlot ? [mockProfile.interviewSlot] : []),
		notificationPreference: Array.isArray(mockProfile.notificationPreference) ?
		mockProfile.notificationPreference :
		(mockProfile.notificationPreference ? [mockProfile.notificationPreference] : []),
		resume: mockProfile.resume || false,
		resumeFileType: mockProfile.resumeFileType || "",
		additionalFiles: mockProfile.additionalFiles || [],
		completed: false
	};

	// DOM Elements
	const editButton = document.getElementById('edit-toggle');
	const candidateNameInput = document.getElementById('name');
	const emailInput = document.getElementById('email');
	const countryCodeInput = document.getElementById('country-code');
	const phoneInput = document.getElementById('phone');
	const linkedinInput = document.getElementById('linkedin');
	const consentCheckbox = document.getElementById('consent');
	const emailError = document.getElementById('email-error');
	const countryCodeError = document.getElementById('country-code-error');
	const phoneError = document.getElementById('phone-error');
	const resumeName = document.querySelector('#widget-profile .resume-name');
	const resumeDate = document.querySelector('#widget-profile .resume-date');
	const resumeVerified = document.querySelector('#widget-profile .resume-card .badge-success');
	const uploadButton = document.querySelector('#widget-profile .upload-button');
	const multiUploadButton = document.querySelector('#widget-profile .multi-upload-button');
	const multiContainer = document.querySelector('#widget-profile .multi-file-container');
	const documentTypeSelect = document.getElementById('document-type');
	const addFileButton = document.getElementById('add-file-btn');
	const consentVerified = document.querySelector('#widget-profile .consent-verified');
	const consentContainer = document.querySelector('#widget-profile .consent-container');
	const termsModal = document.getElementById('terms-modal');
	const privacyModal = document.getElementById('privacy-modal');
	const termsLink = document.getElementById('terms-link');
	const privacyLink = document.getElementById('privacy-link');
	const closeTerms = termsModal.querySelector('.close-modal');
	const closePrivacy = privacyModal.querySelector('.close-modal');
	const acceptTermsBtn = document.getElementById('accept-terms');
	const acceptPrivacyBtn = document.getElementById('accept-privacy');
	const preferredTimeContainer = document.getElementById('preferred-time-container');
	const notificationPrefContainer = document.getElementById('notification-preference-container');
	const downloadResumeBtn = document.getElementById('download-resume-btn');
	const uploadText = document.querySelector('#widget-profile .upload-text');
	const widgetResume = document.getElementById('widget-resume');

	if(appsmith.model.resume){
		widgetResume.style.display = 'block';
	}
	else{
		widgetResume.style.display = 'none';
	}
	// Country data
	const countries = [{
		name: "United States",
		code: "+1"
	}, {
		name: "India",
		code: "+91"
	}, {
		name: "UK",
		code: "+44"
	}, {
		name: "Canada",
		code: "+1"
	}, {
		name: "Australia",
		code: "+61"
	}];
	const validCountryCodes = new Set(countries.map(c => c.code));

	function validateCountryCode(code) {
		const isValidFormat = /^\+\d+$/.test(code);
		if (!code) {
			countryCodeError.textContent = 'Country code is required';
			countryCodeError.style.display = 'block';
			countryCodeInput.classList.add('input-error');
			return false;
		}
		if (!isValidFormat) {
			countryCodeError.textContent = 'Must start with + followed by digits';
			countryCodeError.style.display = 'block';
			countryCodeInput.classList.add('input-error');
			return false;
		}
		if (!validCountryCodes.has(code)) {
			countryCodeError.textContent = 'Please enter a valid country code';
			countryCodeError.style.display = 'block';
			countryCodeInput.classList.add('input-error');
			return false;
		}
		countryCodeError.style.display = 'none';
		countryCodeInput.classList.remove('input-error');
		return true;
	}

	function validatePhone(phone) {
		const digits = phone.replace(/\D/g, '');
		if (!digits) {
			phoneError.textContent = 'Phone number is required';
			phoneError.style.display = 'block';
			phoneInput.classList.add('input-error');
			return false;
		}
		if (digits.length < 7 || digits.length > 15) {
			phoneError.textContent = 'Phone number must be 7–15 digits';
			phoneError.style.display = 'block';
			phoneInput.classList.add('input-error');
			return false;
		}
		phoneError.style.display = 'none';
		phoneInput.classList.remove('input-error');
		return true;
	}

	function parsePhoneNumber(fullNumber) {
		if (!fullNumber || !fullNumber.startsWith('+')) return {
			code: '',
			number: ''
		};
		const sortedCodes = Array.from(validCountryCodes).sort((a, b) => b.length - a.length);
		for (const code of sortedCodes) {
			if (fullNumber.startsWith(code)) {
				const national = fullNumber.substring(code.length).replace(/\D/g, '');
				return {
					code,
					number: national
				};
			}
		}
		if (/^\+\d+$/.test(fullNumber)) {
			const digits = fullNumber.substring(1);
			return {
				code: '+1',
				number: digits
			};
		}
		return {
			code: '',
			number: ''
		};
	}

	function updateMultiSelectTrigger(container) {
		const selectedOptions = Array.from(container.querySelectorAll('input[type="checkbox"]:checked'))
		.map(cb => cb.value);
		const trigger = container.querySelector('.multi-select-trigger');
		trigger.textContent = selectedOptions.length > 0 ?
			selectedOptions.join(', ') :
		'Select options';
	}

	function setInitialValues() {
		linkedinInput.value = formData.linkedin;
		candidateNameInput.value = formData.candidateName;
		emailInput.value = formData.email;
		consentCheckbox.checked = formData.consent;

		const timeOptions = preferredTimeContainer.querySelectorAll('input[type="checkbox"]');
		timeOptions.forEach(opt => {
			opt.checked = formData.interviewSlot.includes(opt.value);
		});
		updateMultiSelectTrigger(preferredTimeContainer);

		const notifyOptions = notificationPrefContainer.querySelectorAll('input[type="checkbox"]');
		notifyOptions.forEach(opt => {
			opt.checked = formData.notificationPreference.includes(opt.value);
		});
		updateMultiSelectTrigger(notificationPrefContainer);

		if (formData.phone) {
			const {
				code,
				number
			} = parsePhoneNumber(formData.phone);
			countryCodeInput.value = code;
			phoneInput.value = number;
		} else {
			countryCodeInput.value = '+1';
			phoneInput.value = '';
		}
		console.log(formData,"form")

		if (formData.resume) {
			resumeName.textContent = 'Uploaded Resume';
			resumeDate.textContent = new Date().toLocaleDateString();
			resumeVerified.style.display = 'inline-flex';
			downloadResumeBtn.style.display = 'block';

			uploadText.textContent = 'Click here to update your resume';
		} else {
			resumeName.textContent = 'No resume uploaded';
			resumeDate.textContent = 'Please upload your resume below';
			resumeVerified.style.display = 'none';
			downloadResumeBtn.style.display = 'none';

			uploadText.textContent = 'Click here to upload your resume';
		}

		if (formData.consent) {
			consentVerified.style.display = 'flex';
		}

		formData.additionalFiles.forEach(file => {
			const card = document.createElement('div');
			card.className = 'multi-file-card';
			const typeName = file.type.replace('_', ' ').replace(/\b\w/g, l => l.toUpperCase());
			card.innerHTML = `
                        <i class="fas fa-file-alt icon" style="color: var(--primary);"></i>
                        <div class="resume-info">
                            <p class="resume-name">${file.name} <span class="badge badge-type">${typeName}</span></p>
                            <p class="resume-date">${new Date().toLocaleDateString()}</p>
                        </div>
                        <button class="delete-file"><i class="fas fa-trash"></i></button>
                        `;
			const deleteBtn = card.querySelector('.delete-file');
			deleteBtn.addEventListener('click', () => {
				multiContainer.removeChild(card);
				formData.additionalFiles = formData.additionalFiles.filter(f => f !== file);
				if (appsmith.updateModel) {
					appsmith.updateModel({
						info: [{ ...formData,
										deleted: false
									 }],
						additionalFiles: formData.additionalFiles
					});
				}
			});
			multiContainer.appendChild(card);
		});
	}

	function isProfileComplete(data) {
		const isNameValid = !!data.candidateName.trim();
		const isEmailValid = !!data.email;
		const isCountryValid = validateCountryCode(data.countryCode);
		const isPhoneValid = validatePhone(data.phone);
		const isConsentGiven = data.consent;
		const isInterviewSlotSelected = data.interviewSlot.length > 0;
		const isNotificationSelected = data.notificationPreference.length > 0;
		return isNameValid && isEmailValid && isCountryValid && isPhoneValid && isConsentGiven && isInterviewSlotSelected && isNotificationSelected;
	}

	function updateProfileStatus() {
		const selectedTime = Array.from(preferredTimeContainer.querySelectorAll('input[type="checkbox"]:checked')).map(cb => cb.value);
		const selectedNotify = Array.from(notificationPrefContainer.querySelectorAll('input[type="checkbox"]:checked')).map(cb => cb.value);
		formData.completed = isProfileComplete({
			candidateName: candidateNameInput.value,
			email: emailInput.value,
			countryCode: countryCodeInput.value,
			phone: phoneInput.value,
			consent: consentCheckbox.checked,
			interviewSlot: selectedTime,
			notificationPreference: selectedNotify,
		});

		const badge = document.getElementById('account-badge');
		const statusText = document.getElementById('account-status-text');
		if (formData.completed) {
			editButton.innerHTML = '<i class="fa-solid fa-floppy-disk icon"></i><span>Save Profile</span>';
			badge.className = "badge badge-success";
			badge.innerHTML = '<i class="fas fa-check-circle small-icon"></i> Active';
			statusText.textContent = "Profile complete and verified";
			consentContainer.style.borderColor = '';
		} else {
			editButton.innerHTML = '<i class="fas fa-user-plus icon"></i><span>Complete Profile</span>';
			badge.className = "badge badge-warning";
			badge.innerHTML = '<i class="fas fa-exclamation-circle small-icon"></i> Incomplete';
			statusText.textContent = "Please complete your profile";
			if (!consentCheckbox.checked) {
				consentContainer.style.borderColor = '#ef4444';
				consentContainer.style.borderWidth = '2px';
			} else {
				consentContainer.style.borderColor = 'var(--border)';
			}
		}
	}

	function saveProfile() {
		if (!consentCheckbox.checked) {
			consentContainer.style.borderColor = '#ef4444';
			consentContainer.style.borderWidth = '2px';
			return;
		}
		const isCountryValid = validateCountryCode(countryCodeInput.value);
		const isPhoneValid = validatePhone(phoneInput.value);
		if (!isCountryValid || !isPhoneValid) return;

		const selectedTime = Array.from(preferredTimeContainer.querySelectorAll('input[type="checkbox"]:checked')).map(cb => cb.value);
		const selectedNotify = Array.from(notificationPrefContainer.querySelectorAll('input[type="checkbox"]:checked')).map(cb => cb.value);
		formData.linkedin = linkedinInput.value;
		formData.candidateName = candidateNameInput.value;
		formData.phone = countryCodeInput.value + phoneInput.value.replace(/\D/g, '');
		formData.consent = consentCheckbox.checked;
		formData.interviewSlot = selectedTime;
		formData.notificationPreference = selectedNotify;

		updateProfileStatus();
		if (appsmith.updateModel) {
			console.log('formData',formData)
			appsmith.updateModel({
				info: [{ ...formData,
								deleted: false
							 }]
			});
		}
		// appsmith.updateModel({  profile: formData})
		if (appsmith.triggerEvent) {
						console.log('formDatatrr',formData,appsmith.model)

			appsmith.triggerEvent("onProfileSave");
		}
	}

	// Modal handlers
	function openModal(modal) {
		modal.style.display = "block";
	}

	function closeModal(modal) {
		modal.style.display = "none";
	}

	termsLink.addEventListener('click', (e) => {
		e.preventDefault();
		openModal(termsModal);
	});
	privacyLink.addEventListener('click', (e) => {
		e.preventDefault();
		openModal(privacyModal);
	});
	closeTerms.addEventListener('click', () => closeModal(termsModal));
	closePrivacy.addEventListener('click', () => closeModal(privacyModal));
	acceptTermsBtn.addEventListener('click', () => {
		closeModal(termsModal);
		consentCheckbox.checked = true;
		consentVerified.style.display = 'flex';
		consentContainer.style.borderColor = 'var(--border)';
		updateProfileStatus();
	});
	acceptPrivacyBtn.addEventListener('click', () => {
		closeModal(privacyModal);
		consentCheckbox.checked = true;
		consentVerified.style.display = 'flex';
		consentContainer.style.borderColor = 'var(--border)';
		updateProfileStatus();
	});
	window.addEventListener('click', (e) => {
		if (e.target === termsModal) closeModal(termsModal);
		if (e.target === privacyModal) closeModal(privacyModal);
	});

	// Multi-select logic
	document.addEventListener('click', (e) => {
		document.querySelectorAll('#widget-profile .multi-select-container').forEach(container => {
			const trigger = container.querySelector('.multi-select-trigger');
			const dropdown = container.querySelector('.multi-select-dropdown');
			if (trigger.contains(e.target)) {
				dropdown.style.display = dropdown.style.display === 'block' ? 'none' : 'block';
			} else if (!container.contains(e.target)) {
				dropdown.style.display = 'none';
			}
		});
	});

	document.addEventListener('change', (e) => {
		if (e.target.type === 'checkbox') {
			const container = e.target.closest('#widget-profile .multi-select-container');
			if (container) {
				updateMultiSelectTrigger(container);
				updateProfileStatus();
			}
		}
	});

	// Event listeners
	editButton.addEventListener('click', saveProfile);
	countryCodeInput.addEventListener('input', () => {
		validateCountryCode(countryCodeInput.value);
		updateProfileStatus();
	});
	phoneInput.addEventListener('input', () => {
		validatePhone(phoneInput.value);
		updateProfileStatus();
	});
	consentCheckbox.addEventListener('change', () => {
		if (consentCheckbox.checked) {
			consentVerified.style.display = 'flex';
			consentContainer.style.borderColor = 'var(--border)';
		} else {
			consentVerified.style.display = 'none';
		}
		updateProfileStatus();
	});
	[candidateNameInput, linkedinInput].forEach(el => {
		el.addEventListener('input', updateProfileStatus);
	});

	// File upload
	async function fileToBase64(file) {
		return new Promise((res, rej) => {
			const reader = new FileReader();
			reader.readAsDataURL(file);
			reader.onload = () => res(reader.result.split(',')[1]);
			reader.onerror = err => rej(err);
		});
	}

	uploadButton.addEventListener('change', async e => {
		if (e.target.files.length > 0) {
			const file = e.target.files[0];
			const base64 = await fileToBase64(file);
			let fileType = "";
			if (file.name.toLowerCase().endsWith('.pdf')) fileType = "pdf";
			else if (file.name.toLowerCase().endsWith('.docx')) fileType = "docx";
			else if (file.name.toLowerCase().endsWith('.doc')) fileType = "doc";

			formData.resume = true;
			formData.resumeFileType = fileType;
			resumeName.textContent = file.name;
			resumeDate.textContent = new Date().toLocaleDateString();
			resumeVerified.style.display = 'inline-flex';
			downloadResumeBtn.style.display = 'block';
			uploadText.textContent = 'Click here to update your resume';
			if (appsmith.updateModel) {
				appsmith.updateModel({
					info: [{ ...formData,
									deleted: false
								 }],
					resumeFile: base64,
					resumeFileType: fileType
				});
			}
			updateProfileStatus();
		}
	});

	addFileButton.addEventListener('click', () => multiUploadButton.click());
	multiUploadButton.addEventListener('change', async e => {
		if (e.target.files.length > 0) {
			const selectedType = documentTypeSelect.value;
			const file = e.target.files[0];
			const base64 = await fileToBase64(file);
			const fileObj = {
				name: file.name,
				base64,
				type: selectedType
			};
			formData.additionalFiles.push(fileObj);

			const card = document.createElement('div');
			card.className = 'multi-file-card';
			const typeName = selectedType.replace('_', ' ').replace(/\b\w/g, l => l.toUpperCase());
			card.innerHTML = `
                        <i class="fas fa-file-alt icon" style="color: var(--primary);"></i>
                        <div class="resume-info">
                            <p class="resume-name">${file.name} <span class="badge badge-type">${typeName}</span></p>
                            <p class="resume-date">${new Date().toLocaleDateString()}</p>
                        </div>
                        <button class="delete-file"><i class="fas fa-trash"></i></button>
                        `;
			const deleteBtn = card.querySelector('.delete-file');
			deleteBtn.addEventListener('click', () => {
				multiContainer.removeChild(card);
				formData.additionalFiles = formData.additionalFiles.filter(f => f !== fileObj);
				if (appsmith.updateModel) {
					appsmith.updateModel({
						info: [{ ...formData,
										deleted: false
									 }],
						additionalFiles: formData.additionalFiles
					});
				}
			});
			multiContainer.appendChild(card);
			if (appsmith.updateModel) {
				appsmith.updateModel({
					info: [{ ...formData,
									deleted: false
								 }],
					additionalFiles: formData.additionalFiles
				});
			}
			multiUploadButton.value = '';
		}
	});

	downloadResumeBtn.addEventListener('click', function() {
		downloadResumeBtn.disabled = true;
		if (typeof appsmith !== 'undefined' && appsmith.triggerEvent) {
			appsmith.triggerEvent("onDownloadResumeClick");
		}
		setTimeout(() => {
			downloadResumeBtn.disabled = false;
		}, 1000);
	});

	setInitialValues();
	updateProfileStatus();
}

// ===================================
// 4. WIDGET 3 (Custom5) LOGIC
// Renamed initApp to initResumeApp
// ===================================
function initResumeApp() {
	const data = (appsmith.model && appsmith.model.data && appsmith.model.data[0]) || {};
	console.log("Candidate Data:", data);

	const candidateData = {
		name: data.candidateName || "N/A",
		email: data.email || "No email provided",
		phone: data.phone || "No phone number",
		location: data.location || "Location not specified",
		avatar: data.avatar ||
		"https://images.unsplash.com/photo-1494790108377-be9c29b29330?w=150&h=150&fit=crop&crop=face",
		position: data.position || "Not Specified",
		summary: data.summary || "Professional summary not available",
		skills: Array.isArray(data.skills) ?
		data.skills.map((s) => ({
			name: s
		})) :
		[{
			name: "Skills not specified"
		}],
		workExperience: Array.isArray(data.work_experience) ?
		data.work_experience.map((exp) => ({
			title: exp.job_title || "Position not specified",
			company: exp.company_name || "Company not specified",
			duration: exp.start_date && exp.end_date ?
			`${exp.start_date} - ${exp.end_date}` :
			"Duration not specified",
			description: exp.description || "Job description not available",
		})) :
		[{
			title: "Work experience not available",
			company: "Company not specified",
			duration: "Duration not specified",
			description: "No work-experience details provided",
		}, ],
		education: Array.isArray(data.education) ?
		data.education.map((edu) => ({
			degree: edu.degree || "Degree not specified",
			institution: edu.school_name || "Institution not specified",
			year: edu.start_date && edu.end_date ?
			`${edu.start_date} - ${edu.end_date}` :
			"Year not specified",
			gpa: edu.gpa || null,
		})) :
		[{
			degree: "Education information not available",
			institution: "Institution not specified",
			year: "Year not specified",
		}, ],
	};

	// RENDER FUNCTIONS
	function populateUI() {
		const el = (id) => document.querySelector(`#widget-resume #${id}`);
		if (el("candidate-name")) el("candidate-name").textContent = candidateData.name;
		if (el("candidate-position")) el("candidate-position").textContent = candidateData.position;
		if (el("candidate-email")) el("candidate-email").textContent = candidateData.email;
		if (el("candidate-phone")) el("candidate-phone").textContent = candidateData.phone;
		if (el("candidate-location")) el("candidate-location").textContent = candidateData.location;
		if (el("candidate-summary")) el("candidate-summary").textContent = candidateData.summary;

		const avatar = el("candidate-avatar");
		const fallback = el("avatar-fallback");

		if (avatar && fallback) {
			// 1. Always hide the image tag
			avatar.style.display = "none";

			// 2. Always show the fallback circle
			fallback.style.display = "flex";

			// 3. Get the first letter of the name
			const firstLetter = candidateData.name ? candidateData.name.charAt(0).toUpperCase() : "N/A";
			fallback.textContent = firstLetter;

			// 4. Apply styling
			fallback.style.background = "linear-gradient(135deg, #667eea 0%, #764ba2 100%)";
			fallback.style.color = "white";
			fallback.style.fontSize = "44px";
			fallback.style.fontWeight = "400";
			fallback.style.borderRadius = "50%";
			fallback.style.width = "96px";
			fallback.style.height = "96px";
			fallback.style.alignItems = "center";
			fallback.style.justifyContent = "center";
		}
	}

	function populateSkills() {
		const container = document.querySelector("#widget-resume #skills-container");
		if (!container) return;
		container.innerHTML = "";
		candidateData.skills.forEach((skill) => {
			container.innerHTML += `
                <div class="skill-item">
                  <div class="skill-header">
                    <span class="skill-span">${skill.name}</span>
                  </div>
                </div>`;
		});
	}

	function populateExperience() {
		const container = document.querySelector("#widget-resume #experience-container");
		if (!container) return;
		container.innerHTML = "";
		candidateData.workExperience.forEach((exp) => {
			container.innerHTML += `
                <div class="experience-item">
                  <div class="experience-icon"><i class="fas fa-briefcase"></i></div>
                  <div class="experience-content">
                    <h4>${exp.title}</h4>
                    <p>${exp.company} • ${exp.duration}</p>
                    <p>${exp.description}</p>
                  </div>
                </div>`;
		});
	}

	function populateEducation() {
		const container = document.querySelector("#widget-resume #education-container");
		if (!container) return;
		container.innerHTML = "";
		candidateData.education.forEach((edu) => {
			container.innerHTML += `
                <div class="education-item">
                  <div class="education-icon"><i class="fas fa-graduation-cap"></i></div>
                  <div class="education-content">
                    <h4>${edu.degree}</h4>
                    <p>${edu.institution}</p>
                    <p>${edu.year}${edu.gpa ? " • GPA: " + edu.gpa : ""}</p>
                  </div>
                </div>`;
		});
	}
	const widgetResume = document.getElementById('widget-resume');

	if(appsmith.model.resume){
		widgetResume.style.display = 'block';
	}
	else{
		widgetResume.style.display = 'none';
	}
	// RENDER ALL
	populateUI();
	populateSkills();
	populateExperience();
	populateEducation();
}


// ===================================
// 5. MAIN APPSMITH INITIALIZER
// This runs all widget logic.
// ===================================
appsmith.onReady(() => {
	console.log("Combined widget ready. Initializing components.");

	// Init Sidebar (from Custom3)
	try {
		new Sidebar();
			console.log("Combined widget ready. Initializing components sidebar.");

	} catch (e) {
		console.error("Failed to initialize Sidebar:", e);
	}

	// Init Profile (from Custom1, preserving its original 3s timeout)
	setTimeout(() => {
		try {
			initProfileApp();			console.log("Combined widget ready. Initializing components 3000.");

		} catch (e) {
			console.error("Failed to initialize Profile App:", e);
		}
	}, 3000);


	// Init Resume (from Custom5, preserving its original 1.5s timeout)
	setTimeout(() => {
		try {
			initResumeApp();
						console.log("Combined widget ready. Initializing components 1500.");

		} catch (e) {
			console.error("Failed to initialize Resume App:", e);
		}
	}, 1500);
});

// --- Model Change Handler ---
appsmith.onModelChange(() => {
	console.log("Model changed, re-running resume app init.>0",appsmith.model);
	try {
		// Only the resume app needs to update on model change
		if(appsmith.model.resume){
				console.log("Model changed, re-running resume app init.>0 if",appsmith.model);

		initResumeApp();}
	} catch (e) {
		console.error("Failed to re-initialize Resume App on model change:", e);
	}
});
