onDownloadResume:
{{
  getResume.run(
    () => {
      download(
        atob(getResume.data.fileData),
        appsmith.user.email + "_resume." + getCandidate.data[0].resumeFileType,
        "application/" + getCandidate.data[0].resumeFileType
      );
    },
    () => {
      showAlert('File not found or download failed', 'error');
    }
  )
}}

onProfileSave:
{{
storeValue('isLoading', true);
storeValue('isResume', false);
updateCandidate.run()
  .then(() => {

    if (Custom1.model.resumeFile) {
      return parseResume.run().then(() => uploadResume.run());
    } else {
      console.log("No resume file to parse or upload");
    }
  })
  .catch(() => {
    // Handle error here if needed
  })
  .finally(() => {
		storeValue('isResume', true);
		getCandidate.run()

    storeValue('isLoading', false);
    showAlert("Profile Updated!", "success");
  });
}}

