function getUSDate(date = new Date()) {
  const month = String(date.getMonth() + 1).padStart(2, "0");
  const day = String(date.getDate()).padStart(2, "0");
  const year = date.getFullYear();
  return `${month}-${day}-${year}`;
}

function initApp() {
  // --- Fetch data safely ---
  const data = (appsmith.model && appsmith.model.data && appsmith.model.data[0]) || {};
  console.log("Candidate Data:", data);

  // --- Build candidate object ---
  const candidateData = {
    name: data.candidateName || "N/A",
    email: data.email || "No email provided",
    phone: data.phone || "No phone number",
    location: data.location || "Location not specified",
    avatar:
      data.avatar ||
      "https://images.unsplash.com/photo-1494790108755-2616b612b786?w=150&h=150&fit=crop&crop=face",
    position: data.position || "Not Specified",
    summary: data.summary || "Professional summary not available",
    skills: Array.isArray(data.skills)
      ? data.skills.map((s) => ({ name: s }))
      : [{ name: "Skills not specified" }],
    workExperience: Array.isArray(data.work_experience)
      ? data.work_experience.map((exp) => ({
          title: exp.job_title || "Position not specified",
          company: exp.company_name || "Company not specified",
          duration:
            exp.start_date && exp.end_date
              ? `${exp.start_date} - ${exp.end_date}`
              : "Duration not specified",
          description: exp.description || "Job description not available",
        }))
      : [
          {
            title: "Work experience not available",
            company: "Company not specified",
            duration: "Duration not specified",
            description: "No work experience details provided",
          },
        ],
    education: Array.isArray(data.education)
      ? data.education.map((edu) => ({
          degree: edu.degree || "Degree not specified",
          institution: edu.school_name || "Institution not specified",
          year:
            edu.start_date && edu.end_date
              ? `${edu.start_date} - ${edu.end_date}`
              : "Year not specified",
          gpa: edu.gpa || null,
        }))
      : [
          {
            degree: "Education information not available",
            institution: "Institution not specified",
            year: "Year not specified",
          },
        ],
  };

  // --- RENDER FUNCTIONS ---
  function populateUI() {
    // Basic Info
    document.getElementById("candidate-name").textContent = candidateData.name;
    document.getElementById("candidate-position").textContent = candidateData.position;
    document.getElementById("candidate-email").textContent = candidateData.email;
    document.getElementById("candidate-phone").textContent = candidateData.phone;
    document.getElementById("candidate-location").textContent = candidateData.location;
    document.getElementById("candidate-summary").textContent = candidateData.summary;

    // ---- Avatar Handling ----
    const avatar = document.getElementById("candidate-avatar");
    const fallback = document.getElementById("avatar-fallback");

    if (avatar && fallback) {
      if (candidateData.avatar && candidateData.avatar.trim() !== "") {
        avatar.src = candidateData.avatar;
        avatar.style.display = "block";
        fallback.style.display = "none";

        avatar.onerror = () => {
          avatar.style.display = "none";
          fallback.style.display = "flex";
          fallback.textContent = candidateData.name
            ? candidateData.name.charAt(0).toUpperCase()
            : "N/A";
        };
      } else {
        avatar.style.display = "none";
        fallback.style.display = "flex";
        fallback.textContent = candidateData.name
          ? candidateData.name.charAt(0).toUpperCase()
          : "N/A";
      }

      fallback.style.background = "linear-gradient(135deg, #667eea 0%, #764ba2 100%)";
      fallback.style.color = "white";
      fallback.style.fontSize = "44px";
      fallback.style.fontWeight = "400";
      fallback.style.borderRadius = "50%";
      fallback.style.width = "96px";
      fallback.style.height = "96px";
      fallback.style.alignItems = "center";
      fallback.style.justifyContent = "center";
      fallback.style.display = "none";
    }
  }

  function populateSkills() {
    const container = document.getElementById("skills-container");
    if (!container) return;
    container.innerHTML = "";

    candidateData.skills.forEach((skill) => {
      container.innerHTML += `
        <div class="skill-item">
          <div class="skill-header">
            <span class="skill-span">${skill.name}</span>
          </div>
        </div>`;
    });
  }

  function populateExperience() {
    const container = document.getElementById("experience-container");
    if (!container) return;
    container.innerHTML = "";

    candidateData.workExperience.forEach((exp) => {
      container.innerHTML += `
        <div class="experience-item">
          <div class="experience-icon"><i class="fas fa-briefcase"></i></div>
          <div class="experience-content">
            <h4>${exp.title}</h4>
            <p>${exp.company} â€¢ ${exp.duration}</p>
            <p>${exp.description}</p>
          </div>
        </div>`;
    });
  }

  function populateEducation() {
    const container = document.getElementById("education-container");
    if (!container) return;
    container.innerHTML = "";

    candidateData.education.forEach((edu) => {
      container.innerHTML += `
        <div class="education-item">
          <div class="education-icon"><i class="fas fa-graduation-cap"></i></div>
          <div class="education-content">
            <h4>${edu.degree}</h4>
            <p>${edu.institution}</p>
            <p>${edu.year}${edu.gpa ? " â€¢ GPA: " + edu.gpa : ""}</p>
          </div>
        </div>`;
    });
  }

  // --- RENDER ALL ---
  populateUI();
  populateSkills();
  populateExperience();
  populateEducation();
}

// --- RUN APP ---
document.addEventListener("DOMContentLoaded", () => {
  setTimeout(initApp, 1500);
});
appsmith.onModelChange(initApp);
