// Sidebar functionality for Appsmith
// Triggers a unique Appsmith event for each menu item
class Sidebar {
  constructor() {
    this.sidebar = document.getElementById('sidebar');
    this.navLinks = document.querySelectorAll('.nav-link');
    this.init();
  }
  init() {
    this.setupEventListeners();
  }
  setupEventListeners() {
    this.navLinks.forEach(link => {
      link.addEventListener('click', e => {
        e.preventDefault();
        const page = link.getAttribute('data-page');
        this.setActiveLink(link);
        // Fire a unique Appsmith event for each menu item
        if (typeof appsmith !== 'undefined' && appsmith.triggerEvent) {
          switch (page) {
            case 'jobs':
              appsmith.triggerEvent('onJobs', {
                page: 'jobs'
              });
              break;
            case 'messages':
              appsmith.triggerEvent('onMessages', {
                page: 'messages'
              });
              break;
            case 'profile':
              appsmith.triggerEvent('onProfile', {
                page: 'profile'
              });
              break;
            case 'help':
              appsmith.triggerEvent('onHelp', {
                page: 'help'
              });
              break;
            default:
              appsmith.triggerEvent('onSidebarNav', {
                page
              });
          }
        }
      });
    });
  }
  setActiveLink(activeLink) {
    this.navLinks.forEach(link => {
      link.classList.remove('active');
    });
    activeLink.classList.add('active');
  }
}

// Initialize sidebar when DOM is loaded or with appsmith onReady
if (typeof appsmith !== 'undefined' && appsmith.onReady) {
  appsmith.onReady(() => {
    new Sidebar();
  });
} else {
  document.addEventListener('DOMContentLoaded', () => {
    new Sidebar();
  });
}

